const request = require('request');

const options = {
  method: 'POST',
  url: 'http://undefinedundefined/payouts',
  headers: {Authorization: 'Bearer REPLACE_BEARER_TOKEN'},
  body: {
    payoutId: 'f4401bd2-1568-4140-bf2d-eb77d2b2b639',
    amount: '15.21',
    currency: 'ZMW',
    country: 'ZMB',
    correspondent: 'MTN_MOMO_ZMB',
    recipient: {type: 'MSISDN', address: {value: '256780334452'}},
    customerTimestamp: '2020-02-21T17:32:28Z',
    statementDescription: 'Up to 22 chars note'
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

